head.script:
var timer
var position;
var preLoad;

function onload(){

	GE("bgm").streamStatus="play";
	browser.setInterval("BGMrp();",18000,0);
	
    position=0;
    	preLoad = new Array(4);
    	
    	
preLoad[0] = "images/garden-staircase-kyoto-japan.jpg";
preLoad[1] = "images/japanese_garden_royal_roads_university_british_columbia.jpg";
preLoad[2] = "images/japanese_garden5.jpg";
preLoad[3] = "images/ninnaji_temple_-_kyoto_japan.jpg";



/*
for (var i=0 ; i<5; i++) {
   	 preLoad[i] = "images/pic" + i + ".JPG";
}*/

caption = new Array(4);

caption[0]="Staircase in Kyoto";
caption[1]="Imperial Garden";
caption[2]="Garden;
caption[3]="Ninnaji Temple, Kyoto";


startShow();
}

function startShow() {
        timer = setTimeout("forward()", 7000);
}

function restartShow() {
    browser.clearTimer(timer);
    startShow();
}
        
function forward() {
    if (position < 4) {
            position++;
    } else {
        position = 0;
    }

    $("img").data = preLoad[position];
    $("text").firstChild.data= caption[position];
    timer = setTimeout("forward()", 7000);
}

function onunload(){
	browser.clearTimer(timer);
}

function $(id) {
		return document.getElementById(id);
	}

function setTimeout(code,delay){
         return   browser.setInterval(code,delay,1);
        }


function GE(id){
	return document.getElementById(id);
}
function BGMrp(){
	GE("bgm").streamStatus="stop";
	GE("bgm").streamStatus="play";
}

function goback(){ browser.launchDocument("../index.xml", "cut");	}
